/*閲嶆柊鍙戦€�*/
var countdown = 60;
function settime(val) {
    if (countdown == 0) {
        val.removeAttribute("disabled");
        val.value = "鍙戦€侀獙璇佺爜";
        countdown = 60;
    } else {
        val.setAttribute("disabled", true);
        val.value = "閲嶆柊鍙戦€�(" + countdown + ")";
        countdown--;
    }
    setTimeout(function () {
        settime(val)
    }, 1000)
}
